    { dishName: "玉米排骨汤", description: "清甜滋补，汤鲜肉嫩。", ingredients: ["排骨", "甜玉米", "胡萝卜", "姜片", "料酒", "枸杞"], steps: ["1. 焯水：排骨冷水下锅焯水，煮出血沫捞出温水洗净。", "2. 炖煮：砂锅加足量水，放入排骨、姜片、料酒。大火烧开转小火盖盖炖1小时。", "3. 加料：放入玉米段和胡萝卜块再炖30分钟。", "4. 调味：加盐调味，出锅前撒几粒枸杞。", "5. 享用：汤清甜，肉软烂。"] },
    { dishName: "罗宋汤", description: "酸甜浓郁，西式风味。", ingredients: ["牛腩", "番茄", "洋葱", "土豆", "卷心菜", "黄油", "番茄酱"], steps: ["1. 煮肉：牛腩切块煮软备用（保留牛肉汤）。", "2. 炒料：黄油化开，炒香洋葱丝和番茄块，炒出红油。", "3. 煮汤：加入牛肉汤和牛肉块。", "4. 加菜：先下土豆块胡萝卜块煮软，最后下卷心菜丝。", "5. 调味：加大量番茄酱、盐、糖、黑胡椒粉调味，熬煮至汤汁浓郁。"] },
    { dishName: "莲藕排骨汤", description: "湖北名菜，粉糯香甜。", ingredients: ["粉藕", "排骨", "姜片", "红枣", "花生"], steps: ["1. 选材：选用七孔粉藕。排骨焯水洗净。", "2. 处理：莲藕切滚刀块，用盐拌一下（防止氧化变黑）。", "3. 煨汤：所有食材（排骨、藕、花生、姜）放入砂锅，加足水。", "4. 时间：大火烧开小火煨2小时以上，至藕粉糯拉丝，汤色粉红。", "5. 调味：最后加盐调味。"] },
    { dishName: "菠菜猪肝汤", description: "补铁明目，汤清味鲜。", ingredients: ["猪肝", "菠菜", "姜丝", "枸杞", "淀粉"], steps: ["1. 腌肝：猪肝切薄片，多漂洗去血水至发白，挤干。加盐、料酒、淀粉腌制。", "2. 焯水：菠菜焯水去草酸，捞出。", "3. 煮汤：清水加姜丝煮开。", "4. 烫肝：放入猪肝快速拨散，煮约1分钟。", "5. 合并：猪肝变色后立即放入菠菜和枸杞，加盐和香油关火，保持猪肝嫩滑。"] },
    { dishName: "丝瓜蛋汤", description: "清热解暑，鲜美清淡。", ingredients: ["丝瓜", "鸡蛋", "姜丝", "盐"], steps: ["1. 备菜：丝瓜去皮切滚刀块，鸡蛋打散。", "2. 炒瓜：热油炒鸡蛋凝固盛出。底油炒软丝瓜。", "3. 煮汤：加入开水煮沸，丝瓜变软。", "4. 合并：倒入鸡蛋块。", "5. 调味：加盐和少许味精，滴香油出锅。"] },
    { dishName: "皮蛋黄瓜汤", description: "快手汤，意外鲜美。", ingredients: ["皮蛋", "黄瓜", "姜丝", "火腿肠"], steps: ["1. 切片：黄瓜切菱形片，皮蛋切丁，火腿切丁。", "2. 炒制：热油下姜丝皮蛋丁煎一下，煎出焦香。", "3. 煮汤：倒入开水，汤色瞬间变白。", "4. 加料：下入黄瓜片和火腿丁煮2分钟。", "5. 调味：加盐、白胡椒粉调味即可。"] },
    { dishName: "萝卜丝煎蛋汤", description: "白萝卜赛人参，汤鲜味美。", ingredients: ["白萝卜", "鸡蛋", "虾皮", "葱花", "白胡椒粉"], steps: ["1. 备菜：白萝卜擦丝，鸡蛋煎成荷包蛋。", "2. 煮汤：锅中不放油炒软萝卜丝，加入开水。", "3. 炖煮：放入荷包蛋，撒一勺虾皮。", "4. 时间：大火煮5-8分钟，汤色奶白。", "5. 调味：加盐、白胡椒粉（去萝卜气）、葱花。"] },
    { dishName: "疙瘩汤", description: "面食与汤的结合，暖心。", ingredients: ["面粉", "西红柿", "青菜", "鸡蛋", "香油"], steps: ["1. 拌面：面粉中极细的水流边倒边搅拌，拌成均匀的小面絮（不要成团）。", "2. 炒汤：炒香西红柿出汁，加水煮开。", "3. 下面：分次撒入面絮，边撒边搅防止粘连。", "4. 煮熟：煮2-3分钟面絮熟透。", "5. 加料：淋入蛋液，撒青菜碎，加盐、香油调味。"] },
    { dishName: "海带排骨汤", description: "补钙降压，经典搭配。", ingredients: ["排骨", "干海带", "姜片", "醋"], steps: ["1. 泡发：干海带泡发洗净切片。排骨焯水。", "2. 炖煮：排骨、海带、姜片入锅，滴几滴醋（利于钙质释放）。", "3. 时间：炖煮1.5小时至排骨酥烂。", "4. 调味：加盐调味即可。"] },
    { dishName: "鸭架冬瓜汤", description: "烤鸭二吃，清淡鲜美。", ingredients: ["烤鸭架", "冬瓜", "姜片", "胡椒粉"], steps: ["1. 熬汤：鸭架拆块，加足量水、姜片大火熬煮30分钟至汤白。", "2. 煮瓜：放入冬瓜块煮软。", "3. 调味：鸭架有咸味，少加盐，多加白胡椒粉。"] },
    { dishName: "黄豆猪手汤", description: "胶原蛋白，软糯Q弹。", ingredients: ["猪蹄", "黄豆", "甜玉米", "姜片"], steps: ["1. 处理：猪蹄焯水，黄豆泡发。", "2. 炖煮：全部食材入高压锅，加水没过。", "3. 压制：上汽压40分钟，至黄豆粉糯，猪蹄脱骨。", "4. 调味：开盖加盐煮2分钟。"] },
    
    // 甜汤/糖水
    { dishName: "银耳莲子羹", description: "胶质满满，滋阴润肺。", ingredients: ["干银耳", "莲子", "红枣", "枸杞", "冰糖"], steps: ["1. 泡发：银耳冷水泡发2小时，撕成小朵（越碎越出胶）。", "2. 炖煮：银耳加足够水，大火烧开转小火慢炖1小时。", "3. 加料：放入莲子、红枣再炖30分钟。", "4. 调味：出锅前10分钟放冰糖和枸杞，搅拌至汤汁粘稠。"] },
    { dishName: "红豆沙", description: "绵密香甜，经典糖水。", ingredients: ["红豆", "陈皮", "冰糖"], steps: ["1. 浸泡：红豆泡一晚。", "2. 煮豆：红豆加陈皮（灵魂）入锅煮至软烂。", "3. 起沙：用勺子按压红豆或过筛去除豆皮，得到细腻豆沙。", "4. 调味：加冰糖融化。"] },
    { dishName: "冰糖雪梨", description: "止咳润肺，清甜滋润。", ingredients: ["雪梨", "冰糖", "川贝(可选)", "枸杞"], steps: ["1. 准备：雪梨去皮切块，或挖空梨心。", "2. 蒸制：梨块放碗中，撒冰糖、枸杞，加少许水。", "3. 时间：隔水蒸40分钟至梨肉透明软烂。"] },
    { dishName: "酒酿圆子", description: "酸甜软糯，桂花飘香。", ingredients: ["糯米小圆子", "甜酒酿", "鸡蛋", "枸杞", "桂花蜜"], steps: ["1. 煮圆子：水开下圆子煮浮起。", "2. 加酒酿：倒入酒酿煮开。", "3. 勾芡：淋少许水淀粉使汤汁微稠（防止沉底）。", "4. 淋蛋：淋入蛋液。", "5. 调味：关火加枸杞和糖桂花。"] },
    { dishName: "绿豆汤", description: "消暑解渴，清热解毒。", ingredients: ["绿豆", "冰糖", "柠檬汁(防变红)"], steps: ["1. 煮制：绿豆洗净加水煮。", "2. 技巧：挤几滴柠檬汁防止氧化变红。", "3. 状态：大火煮开转中小火，煮至绿豆开花。", "4. 调味：加冰糖放凉冷藏饮用。"] }
